    @Override
    public void validate(User user) {
        validateEmail(user.getEmail());
        validatePassword(user.getPassword());
    }

    private void validateEmail(String email) {
        if (!checkValidInput(email, EMAIL_REGEX)) {
            throw new IllegalArgumentException("E-Mail is not match to requirements");
        }
    }

    private void validatePassword(String password) {
        if (!checkValidInput(password, PASSWORD_REGEX)) {
            throw new IllegalArgumentException("Password is not match to requirements");
        }
    }

    private boolean checkValidInput(String input, String regex) {
        return Pattern.matches(regex, input);
    }
